royGUIScriptSingle("GUIInsight_Tech")        self.guiTech = nil    end    if self.guiTeamNames then        GetGUIManager():DestroyGUIScriptSingle("GUIInsight_TeamNames")        self.guiTeamNames = nil    end        if self.guiUnitFrames then        GetGUIManager():DestroyGUIScriptSingle("GUIInsight_PlayerFrames")        self.guiUnitFrames = nil	end    --if self.guiTopBar then    --    GetGUIManager():DestroyGUIScriptSingle("GUIInsight_TopBar")    --    self.guiTopBar = nil    --end    if self.guiTechPoints then        GetGUIManager():DestroyGUIScriptSingle("GUIInsight_TechPoints")        self.guiTechPoints = nil	end    if self.guiLocation then        GetGUIManager():DestroyGUIScriptSingle("GUIInsight_Location")        self.guiLocation = nil	end    if self.guiAlertQueue then        GetGUIManager():DestroyGUIScriptSingle("GUIInsight_AlertQueue")        self.guiAlertQueue = nil	end    --if self.guiProductionBar then    --    GetGUIManager():DestroyGUIScriptSingle("GUIInsight_ProductionBar")    --    self.guiProductionBar = nil    --end    if self.guiPenTool then        GetGUIManager():DestroyGUIScriptSingle("GUIInsight_PenTool")        self.guiPenTool = nil    end    endfunction GUISpectator:SetIsVisible(bool)    self.guiStatistics:SetIsVisible(bool)    self.guiTech:SetIsVisible(bool)    self.guiUnitFrames:SetIsVisible(bool)    --self.guiTopBar:SetIsVisible(bool)    self.guiTechPoints:SetIsVisible(bool)    self.guiLocation:SetIsVisible(bool)    self.guiAlertQueue:SetIsVisible(bool)    --self.guiProductionBar:SetIsVisible(bool)    self.guiPenTool:SetIsVisible(bool)end